CREATE    VIEW dbo.Domino_ECCLOG_DGIRPrintUpdate AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'DGIRPrintUpdate'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$UpdatedBy],[AltContactNo1],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[ASENumber],[Caller],[CallerAgency],[Form],[Incid_Type_Num_Update],[Incident_D],[IncidentArea],[IncidentDetails],[IncidentLocation],[IncidentNumber],[IncidentRegion],[IncidentSubType],[IncidentTimeLogged],[IncidentType],[IncidentUpdateNo],[MCTSNo],[MELPRegion],[NtfReportData],[PrimaryContactNo],[PrimaryContactNoType],[ReportAuthor],[TaskDollarsAssigned],[TaskDollarsIssuedTo],[TaskNumber],[UpdateTimeLogged])) as PivotTable
