CREATE    VIEW dbo.Domino_ECCLOG_DGIRUpdate AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'DGIRUpdate'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Conflict],[$ConflictItems],[$REF],[$Revisions],[$UpdatedBy],[23],[AltContactNo1],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[ASENumber],[AssociatedEvent],[Caller],[CallerAgency],[CurrentdocID],[Delete],[docID],[EMAILTRANSFERTOEPS],[Form],[FormName],[HoldEPSTransfer],[IncidentArea],[IncidentDateTime],[IncidentDetails],[IncidentLocation],[IncidentNumber],[IncidentRegion],[IncidentStatus],[IncidentSubject],[IncidentSubType],[IncidentTimeLogged],[IncidentType],[IncidentUpdateNo],[JournalVoucherTo],[MCTSNo],[MELPRegion],[NtfReportData],[OriginalModTime],[OriginalTimeLogged],[ParentDocID],[ParentLogged],[ParentUniversalID],[PrimaryContactNo],[PrimaryContactNoType],[ReportAuthor],[ReportAuthorPosition],[ReportDisplayDate],[REPORTTRANSFERSTATUS],[ReportUpdateDisplayDate],[SuppressReport],[TaskCategory],[TaskDollarsAssigned],[TaskDollarsIssuedTo],[TaskDollarsRequestorPosition],[TaskNumber],[ThisDocLogged],[TransferToEC],[TransferToEPS],[UniversalID],[UpdateTimeLogged],[UpdateTimeLoggedTEXT])) as PivotTable
