CREATE    VIEW dbo.Domino_ECCLOG_FundingAuth AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'FundingAuth'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Revisions],[$UpdatedBy],[ASENumber],[AuthID],[AuthLimit],[AuthName],[AuthTitle],[Delete],[Form],[OriginalModTime],[TaskNumber])) as PivotTable
