CREATE    VIEW dbo.Domino_ECCLOG_GIR AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'GIR'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Conflict],[$Header],[$REF],[$RespondedTo],[$Revisions],[$UpdatedBy],[AltContactNo1],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[Caller],[CallerAgency],[Delete],[docID],[FaCTS_Update],[Form],[FormName],[GIRNumber],[IncidentArea],[INCIDENTCURRENT],[IncidentDetails],[IncidentRegion],[IncidentResponse],[IncidentSubType],[IncidentTimeLogged],[IncidentTimeLoggedTEXT],[IncidentType],[OriginalModTime],[PORReaders],[PrimaryContactNo],[PrimaryContactNoType],[Readers],[ReportAuthor],[ReportAuthorPosition],[ReportDisplayDate],[SuppressReport],[Temp],[UniversalID])) as PivotTable
