CREATE    VIEW dbo.Domino_ECCLOG_MONESUpdate AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'MONESUpdate'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Conflict],[$Header],[$REF],[$Revisions],[$UpdatedBy],[AltContactNo],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[ASENumber],[AssociatedEvent],[Caller],[CallerAgency],[Delete],[docID],[Form],[FormName],[IncidentArea],[IncidentDetails],[IncidentLocation],[IncidentNumber],[IncidentRegion],[IncidentSubject],[IncidentSubType],[IncidentTimeLogged],[IncidentType],[IncidentUpdateNo],[JournalVoucherTo],[NtfReportData],[OriginalModTime],[OriginalTimeLogged],[ParentDocID],[ParentLogged],[ParentUniversalID],[PrimaryContactNo],[PrimaryContactNoType],[ReportAuthor],[ReportAuthorPosition],[ReportDisplayDate],[ReportUpdateDisplayDate],[SuppressReport],[TaskCategory],[TaskDollarsAssigned],[TaskDollarsIssuedTo],[TaskDollarsRequestorPosition],[TaskNumber],[ThisDocLogged],[UniversalID],[UpdateTimeLogged],[UpdateTimeLoggedTEXT])) as PivotTable
