CREATE    VIEW dbo.Domino_ECCLOG_PrintASE AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'PrintASE'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$UpdatedBy],[ASEAircraftType],[ASEApprovedBy],[ASECompany],[ASEDetails],[ASEIncidentArea],[ASEIncidentLocation],[ASEIncidentNumber],[ASEIncidentSubject],[ASENumber],[ASEOrganization],[ASEPEPRegion],[ASEPrimaryContactNo],[ASEReportAuthor],[ASEReportAuthorPosition],[ASERequestedBy],[ASETaskNumber],[ASETimeLogged],[Form],[IncidentType])) as PivotTable
