CREATE    VIEW dbo.Domino_ECCLOG_PrintGIR AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'PrintGIR'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Fonts],[$UpdatedBy],[AltContactNo1],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[Caller],[CallerAgency],[Form],[Incident_D],[IncidentArea],[IncidentDateTime],[IncidentDetails],[IncidentRegion],[IncidentResponse],[IncidentSubType],[IncidentTimeLogged],[IncidentType],[IncidentType_D],[Line2],[NtfReportData],[PrimaryContactNo],[PrimaryContactNoType],[ReportAuthor])) as PivotTable
