CREATE    VIEW dbo.Domino_ECCLOG_PrintMVISAR AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'PrintMVISAR'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Revisions],[$UpdatedBy],[AltContactNo1],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[ASENumber],[BCASNo],[Caller],[CallerAgency],[Form],[Incid_Type_Num_Sub],[Incident_D],[IncidentArea],[IncidentDateTime],[IncidentDateTimeConcluded],[IncidentDetails],[IncidentLocation],[IncidentNumber],[IncidentSubject],[IncidentSubType],[IncidentTimeLogged],[IncidentType],[NtfReportData],[PrimaryContactNo],[PrimaryContactNoType],[PtpReportData],[RCCNo],[RCMPNo],[ReportAuthor],[TaskDollarsAssigned],[TaskDollarsIssuedTo],[TaskNumber])) as PivotTable
