CREATE    VIEW dbo.Domino_ECCLOG_RMB AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'RMB'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Revisions],[$UpdatedBy],[AltContactNo1],[AltContactNo1Type],[AltContactNo2],[AltContactNo2Type],[AssociatedEvent],[Caller],[CallerAgency],[CopiedToEM2000],[docID],[Form],[FormName],[IncidentArea],[IncidentDateTime],[IncidentDetails],[IncidentLocation],[IncidentNumber],[IncidentRegion],[IncidentSubject],[IncidentSubType],[IncidentTimeLogged],[IncidentType],[NtfReportData],[OriginalModTime],[PrimaryContactNo],[PrimaryContactNoType],[ReportAuthor],[ReportAuthorPosition],[ReportDisplayDate],[SuppressReport],[UniversalID])) as PivotTable
