CREATE    VIEW dbo.Domino_ECCLOG_SRR AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name as 'ColNAme', ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'SRR'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [ColName]IN ([$ConflictAction],[$Revisions],[$UpdatedBy],[AuthorizedDate],[AuthorizedDateTEXT],[CopiedToEM2000],[Copy],[Delete],[Detachment],[docID],[DriverDOB],[DriverName],[DriverNo],[Form],[IncidentArea],[INCIDENTCURRENT],[IncidentDate],[IncidentDateTEXT],[IncidentLocation],[IncidentNumber],[IncidentSubType],[IncidentTime],[IncidentTimeLogged],[IncidentType],[License],[MaleFemale],[Name],[OriginalModTime],[PinBadgeNo],[PrimaryContactNo],[PrimaryContactNoType],[Q1],[Q10],[Q11],[Q12],[Q13],[Q1Text],[Q2],[Q3],[Q4],[Q4Text],[Q6],[Q7],[Q8],[Q9],[Readers],[ReportAuthor],[ReportAuthorPosition],[ReportDisplayDate],[SRRPROCESSED],[TaskCategory],[UniversalID])) as PivotTable