CREATE    VIEW dbo.Domino_ECCLOG_Sample_DGIR AS  SELECT *  FROM (   SELECT j.DocID as 'DocumentID', b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'Sample DGIR'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Revisions],[$UpdatedBy],[ASENumber],[Caller],[Details],[Form],[Ntf1Action],[Ntf1Contact],[Ntf1Details],[Ntf1When],[NtfReportData],[PrimaryContactNumber],[TaskNumber])) as PivotTable
