CREATE    VIEW dbo.[Domino_ReporterLive_Emergency Incident Advisory] AS  SELECT *  FROM (   SELECT j.DocID as Doc_Id, b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'Emergency Incident Advisory'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([$Fonts],[EIAReportApprovalDateTime],[PREOCName],[Parent_UNID],[UpdateNumber],[Universal_ID],[Form],[RelatedIncidentNumber],[EIAReportApproverPosition],[EIAEMail],[tmpFrom],[$Revisions],[EIAReportApproverSignature],[IncidentLocation],[RelatedIncident],[EIASituationOverview],[DateComposed],[ReportNumber],[EIAComments],[From],[EIAReportApprovedBy],[$REF],[EIACriticalRequirements],[$Conflict],[timeComposed],[ReportType],[EIAContactNumber],[EIAResponseActivities],[$UpdatedBy],[$FILE],[ActualName])) as PivotTable
