CREATE    VIEW dbo.[Domino_ReporterLive_Expenditure Amendment] AS  SELECT *  FROM (   SELECT j.DocID as Doc_Id, b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'Expenditure Amendment'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([PEPTaskNumberC],[Universal_ID],[Parent_UNID],[PREOCEXPAuthorizerPosition],[ChargeAgencyC],[IncidentDescriptionAmend],[CrAmtNTE],[MailSentFlag],[AmdAmtNTE],[EventLocationC],[PREOCEXPRequestingAgencyC],[$Revisions],[CrAmtReq],[Form],[RelatedIncidentC],[From],[PREOCExpAuthorizingTime],[EAFNumberC],[RelatedIncidentNumberC],[AmdAmtReq],[PREOCEXPAuthorizingSignatureA_1],[TotAmtReq],[$UpdatedBy],[TotAmtNTE],[ActualName],[PREOCEXPAuthorizingDate],[$REF],[PREOCNameC])) as PivotTable
