CREATE    VIEW dbo.[Domino_ReporterLive_PECC 801A - Briefing Format] AS  SELECT *  FROM (   SELECT j.DocID as Doc_Id, b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'PECC 801A - Briefing Format'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([ActualName],[OpPeriodEndDate],[$REF],[$Fonts],[PECCBriefMediaIssues],[PECCBriefPEPTaskNum],[$UpdatedBy],[PECCBriefPresenter],[PECCBriefSituation],[RelatedIncident],[OpPeriodStartDate],[tmpFrom],[Parent_UNID],[From],[Universal_ID],[OpPeriodEndTime],[RelatedIncidentNumber],[PECCBriefFutureActivities],[Form],[OriginalModTime],[PREOCName],[PECCBriefUnmetNeeds],[OpPeriodStartTime],[PECCBriefDateTime])) as PivotTable
