CREATE    VIEW dbo.[Domino_ReporterLive_PREOC 701A - Briefing Report] AS  SELECT *  FROM (   SELECT j.DocID as Doc_Id, b.Name, ISNULL(b.Text, '') as [Text]   FROM dbo.DominoData j   CROSS APPLY OPENJSON (FormData) a   CROSS APPLY OPENJSON (a.value)    WITH (    [Name] VARCHAR(50)      ,[Text] VARCHAR(MAX)      ,[Values] NVARCHAR(MAX) AS JSON   ) b    where FormName = 'PREOC 701A - Briefing Report'     and isJson(FormData) =1  ) App  PIVOT   (    MAX([Text])  FOR [Name]IN ([Form],[OpPeriodEndTime],[tmpFrom],[PREOCBriefUnmetNeeds],[PREOCBriefPresenter],[$Fonts],[Parent_UNID],[RelatedIncidentNumber],[PREOCBriefSituation],[OpPeriodStartDate],[OpPeriodStartTime],[PREOCName],[Universal_ID],[PREOCBriefMediaIssues],[ActualName],[From],[PREOCBriefPEPTaskNum],[PREOCBriefFutureActivities],[PREOCBriefDateTime],[$REF],[OriginalModTime],[$UpdatedBy],[OpPeriodEndDate],[RelatedIncident])) as PivotTable
